import React from "react";
import { useSelector } from "react-redux";

const initialState = {
  preferiti: [],
};

const preferencesReducer = (state = initialState, action) => {
  switch (action.type) {
    case "AGGIUNGI_PREFERITO":
      return {
        ...state,
        preferiti: [...state.preferiti, action.payload],
      };
    default:
      return state;
  }
};

const Preferiti = () => {
  const preferiti = useSelector((state) => state.preferences.preferiti);

  return (
    <div>
      <h2>Elenco Preferiti</h2>
      <ul>
        {preferiti.map((azienda) => (
          <li key={azienda.id}>{azienda.nome}</li>
        ))}
      </ul>
    </div>
  );
};

export default preferencesReducer;
